function Popup(seting){
	this.modal = document.querySelector(seting.modal);
	this.overlay = document.querySelector(seting.overlay);
	let popup = this;

	this.open = function(content) {
			popup.modal.innerHTML = content;
			popup.overlay.classList.add('openPopup');
			popup.modal.classList.add('openPopup');

	}	

	this.close = function(){
			popup.modal.classList.remove('openPopup');
			popup.overlay.classList.remove('openPopup');
	}

	this.overlay.onclick = popup.close;
}

window.onload = function(){


	let pup = new Popup({
		modal : '.modal',
		overlay : '.overlay'
	}) 

	
	document.querySelector('.request').onclick = function() {
		let form = document.querySelector('.col-my-popup');
		pup.open(form.innerHTML);
	};

}